import React, { useEffect, useRef, useCallback } from 'react';
import { useReducedMotion } from 'motion/react';

// Redesigned CircuitField: subtle but technical
// - Thick power rail shows electron flow (âˆ’) and V/I indicators
// - Several low-voltage signal lines with positive carriers (+) easily pushed by a positive cursor
// - Tasteful random pulses and electronics symbols (resistor, capacitor, diode, op-amp, ground)
// - Performance: dual-canvas, DPR-capped, offscreen pause, reduced-motion static fallback

type Vec2 = { x: number; y: number };

type Signal = {
  points: Array<{ x0: number; y0: number; x: number; y: number }>; // control points
  segs: Array<{ ax: Vec2; bx: Vec2; len: number }>;
  total: number;
  carriers: Array<{ s: number; v: number; charge: 1 | -1 }>; // s in [0,total]
  color: string;
  tubeR: number; // visual comfort radius
  label: string; // e.g. 1.2V, 3.3V
  stiffness: number; // curve wobble stiffness (higher -> stiffer)
};

type SymbolDef =
  | { kind: 'ground'; at: Vec2 }
  | { kind: 'resistor'; at: Vec2; angle: number }
  | { kind: 'capacitor'; at: Vec2; angle: number }
  | { kind: 'diode'; at: Vec2; angle: number }
  | { kind: 'opamp'; at: Vec2; angle: number };

const CircuitField: React.FC = () => {
  const staticRef = useRef<HTMLCanvasElement | null>(null);
  const dynamicRef = useRef<HTMLCanvasElement | null>(null);
  const runningRef = useRef(false);
  const rafRef = useRef<number | null>(null);
  const reduceMotion = useReducedMotion();

  const dprRef = useRef(1);
  const timeRef = useRef(0);
  const mouse = useRef({ x: 0, y: 0, active: false, charge: 1 as 1 | -1 }); // +1 positive probe, Alt toggles -1

  const stateRef = useRef<
    | {
        w: number;
        h: number;
        rail: {
          y: number;
          thickness: number;
          voltage: number; // volts
          carriers: Array<{ x: number; v: number }>;
        };
        signals: Signal[];
        symbols: SymbolDef[];
      }
    | null
  >(null);

  const rand = (a: number, b: number) => Math.random() * (b - a) + a;
  const clamp = (v: number, lo: number, hi: number) => Math.max(lo, Math.min(hi, v));

  // Build a left-to-right polyline with slight vertical variation
  const makeSignal = (w: number, y: number, dpr: number, label: string, color: string, stiffness: number, tubeR: number): Signal => {
    const points: Array<{ x0: number; y0: number; x: number; y: number }> = [];
    const cols = 6;
    for (let i = 0; i < cols; i++) {
      const x = (i / (cols - 1)) * (w - 40 * dpr) + 20 * dpr; // 20px margin
      const yj = y + rand(-8, 8) * dpr;
      points.push({ x0: x, y0: yj, x, y: yj });
    }
    // Segments and total length
    const segs: Array<{ ax: Vec2; bx: Vec2; len: number }> = [];
    let total = 0;
    for (let i = 0; i < points.length - 1; i++) {
      const ax = { x: points[i].x, y: points[i].y };
      const bx = { x: points[i + 1].x, y: points[i + 1].y };
      const len = Math.hypot(bx.x - ax.x, bx.y - ax.y);
      segs.push({ ax, bx, len });
      total += len;
    }
    const carriers = new Array(9).fill(0).map((_, i) => ({ s: (i / 9) * total, v: rand(0.045, 0.08) * dpr, charge: 1 as 1 }));
    return { points, segs, total, carriers, color, tubeR, label, stiffness };
  };

  const pointAt = (sig: Signal, s: number): Vec2 => {
    // wrap s into [0,total]
    let d = s % sig.total; if (d < 0) d += sig.total;
    for (let i = 0; i < sig.segs.length; i++) {
      const seg = sig.segs[i];
      if (d <= seg.len) {
        const t = seg.len ? d / seg.len : 0;
        return { x: seg.ax.x + (seg.bx.x - seg.ax.x) * t, y: seg.ax.y + (seg.bx.y - seg.ax.y) * t };
      }
      d -= seg.len;
    }
    // fallback end
    const last = sig.segs[sig.segs.length - 1];
    return { x: last.bx.x, y: last.bx.y };
  };

  const drawSymbols = (ctx: CanvasRenderingContext2D, syms: SymbolDef[], dpr: number) => {
    ctx.save();
    ctx.strokeStyle = 'rgba(148,163,184,0.55)'; // slate-400
    ctx.fillStyle = 'rgba(148,163,184,0.7)';
    ctx.lineWidth = 1 * dpr;
    for (const s of syms) {
      if (s.kind === 'ground') {
        const { x, y } = s.at;
        ctx.beginPath(); ctx.moveTo(x, y - 10 * dpr); ctx.lineTo(x, y - 2 * dpr); ctx.stroke();
        ctx.beginPath(); ctx.moveTo(x - 6 * dpr, y); ctx.lineTo(x + 6 * dpr, y); ctx.stroke();
        ctx.beginPath(); ctx.moveTo(x - 4 * dpr, y + 3 * dpr); ctx.lineTo(x + 4 * dpr, y + 3 * dpr); ctx.stroke();
        ctx.beginPath(); ctx.moveTo(x - 2 * dpr, y + 6 * dpr); ctx.lineTo(x + 2 * dpr, y + 6 * dpr); ctx.stroke();
      } else if (s.kind === 'resistor') {
        const { x, y } = s.at; const ang = s.angle;
        const ux = Math.cos(ang), uy = Math.sin(ang);
        const px = -uy, py = ux;
        ctx.beginPath();
        const L = 24 * dpr; const off = 4 * dpr;
        ctx.moveTo(x - ux * L, y - uy * L);
        for (let i = -5; i <= 5; i++) {
          const zig = (i % 2 === 0 ? off : -off);
          const t = (i / 5) * (L * 0.7);
          ctx.lineTo(x + ux * t + px * zig, y + uy * t + py * zig);
        }
        ctx.lineTo(x + ux * L, y + uy * L);
        ctx.stroke();
      } else if (s.kind === 'capacitor') {
        const { x, y } = s.at; const ang = s.angle;
        const ux = Math.cos(ang), uy = Math.sin(ang);
        const px = -uy, py = ux;
        const L = 9 * dpr, plate = 6 * dpr;
        ctx.beginPath(); ctx.moveTo(x - ux * L + px * plate, y - uy * L + py * plate);
        ctx.lineTo(x - ux * L - px * plate, y - uy * L - py * plate); ctx.stroke();
        ctx.beginPath(); ctx.moveTo(x + ux * L + px * plate, y + uy * L + py * plate);
        ctx.lineTo(x + ux * L - px * plate, y + uy * L - py * plate); ctx.stroke();
      } else if (s.kind === 'diode') {
        const { x, y } = s.at; const ang = s.angle;
        const ux = Math.cos(ang), uy = Math.sin(ang);
        const px = -uy, py = ux;
        const L = 16 * dpr;
        // triangle
        ctx.beginPath();
        ctx.moveTo(x - ux * L * 0.6, y - uy * L * 0.6);
        ctx.lineTo(x, y);
        ctx.lineTo(x - px * L * 0.5 - ux * L * 0.2, y - py * L * 0.5 - uy * L * 0.2);
        ctx.closePath(); ctx.stroke();
        // bar
        ctx.beginPath(); ctx.moveTo(x + ux * L * 0.4 + px * L * 0.5, y + uy * L * 0.4 + py * L * 0.5);
        ctx.lineTo(x + ux * L * 0.4 - px * L * 0.5, y + uy * L * 0.4 - py * L * 0.5); ctx.stroke();
      } else if (s.kind === 'opamp') {
        const { x, y } = s.at; const ang = s.angle;
        const ux = Math.cos(ang), uy = Math.sin(ang);
        const px = -uy, py = ux;
        const L = 22 * dpr;
        // triangle body
        ctx.beginPath();
        ctx.moveTo(x - ux * L, y - uy * L);
        ctx.lineTo(x + px * L * 0.6, y + py * L * 0.6);
        ctx.lineTo(x - px * L * 0.6, y - py * L * 0.6);
        ctx.closePath(); ctx.stroke();
        // + and - inputs
        ctx.font = `${Math.max(9, 10 * dpr)}px system-ui, -apple-system, Segoe UI, Roboto, sans-serif`;
        ctx.textAlign = 'center'; ctx.textBaseline = 'middle';
        ctx.fillText('+', x - px * L * 0.9, y - py * L * 0.9);
        ctx.fillText('-', x + px * L * 0.9, y + py * L * 0.9);
      }
    }
    ctx.restore();
  };

  const init = useCallback(() => {
    const sCanvas = staticRef.current;
    const dCanvas = dynamicRef.current;
    if (!sCanvas || !dCanvas) return;

    const rect = sCanvas.getBoundingClientRect();
    const dpr = Math.min(window.devicePixelRatio || 1, 1.75); // cap for perf
    dprRef.current = dpr;
    const w = Math.max(1, Math.floor(rect.width * dpr));
    const h = Math.max(1, Math.floor(rect.height * dpr));
    sCanvas.width = w; sCanvas.height = h;
    dCanvas.width = w; dCanvas.height = h;

    // Power rail
    const railY = Math.round(h * 0.22);
    const rail = {
      y: railY,
      thickness: Math.max(3, 4 * dpr),
      voltage: 5.0,
      carriers: new Array(18).fill(0).map(() => ({ x: Math.random() * w, v: (0.07 + Math.random() * 0.05) * dpr }))
    };

    // Signals: lower voltage -> easier to deflect (lower stiffness)
    const s1 = makeSignal(w, Math.round(h * 0.42), dpr, '0.8V', 'rgba(56,189,248,0.7)', 0.06, 8 * dpr);
    const s2 = makeSignal(w, Math.round(h * 0.56), dpr, '1.2V', 'rgba(34,197,94,0.65)', 0.08, 9 * dpr);
    const s3 = makeSignal(w, Math.round(h * 0.70), dpr, '3.3V', 'rgba(168,85,247,0.6)', 0.12, 10 * dpr);
    const signals = [s1, s2, s3];

    // Place some symbols aesthetically along signals
    const symbols: SymbolDef[] = [];
    // Along s3: resistor and capacitor to ground
    const pR = pointAt(s3, s3.total * 0.35); symbols.push({ kind: 'resistor', at: pR, angle: 0 });
    const pC = { x: pR.x + 48 * dpr, y: pR.y }; symbols.push({ kind: 'capacitor', at: pC, angle: Math.PI / 2 });
    // Diode on s2
    const pD = pointAt(s2, s2.total * 0.6); symbols.push({ kind: 'diode', at: pD, angle: 0 });
    // Op-amp floating mid
    symbols.push({ kind: 'opamp', at: { x: Math.round(w * 0.78), y: Math.round(h * 0.48) }, angle: 0 });
    // Ground near bottom left
    symbols.push({ kind: 'ground', at: { x: Math.round(w * 0.08), y: Math.round(h * 0.88) } });

    stateRef.current = { w, h, rail, signals, symbols };

    // Static background: grid, power rail base, symbols, labels
    const sctx = sCanvas.getContext('2d');
    if (!sctx) return;
    sctx.clearRect(0, 0, w, h);

    // PCB grid
    sctx.save();
    sctx.strokeStyle = 'rgba(8,145,178,0.18)';
    sctx.lineWidth = 1 * dpr;
    const step = 72 * dpr;
    for (let x = 0; x < w; x += step) { sctx.beginPath(); sctx.moveTo(x, 0); sctx.lineTo(x, h); sctx.stroke(); }
    for (let y = 0; y < h; y += step) { sctx.beginPath(); sctx.moveTo(0, y); sctx.lineTo(w, y); sctx.stroke(); }
    sctx.restore();

    // Power rail line (thicker, subtle gradient)
    const grad = sctx.createLinearGradient(0, rail.y, w, rail.y);
    grad.addColorStop(0, 'rgba(239,68,68,0.35)');
    grad.addColorStop(1, 'rgba(239,68,68,0.55)');
    sctx.strokeStyle = grad;
    sctx.lineWidth = rail.thickness;
    sctx.beginPath(); sctx.moveTo(0, rail.y); sctx.lineTo(w, rail.y); sctx.stroke();
    // Plus marks and voltage label
    sctx.fillStyle = 'rgba(239,68,68,0.85)';
    sctx.font = `${Math.max(10, 11 * dpr)}px system-ui, -apple-system, Segoe UI, Roboto, sans-serif`;
    sctx.textAlign = 'left'; sctx.textBaseline = 'middle';
    for (let x = 24 * dpr; x < w; x += 110 * dpr) sctx.fillText('+', x, rail.y - 7 * dpr);
    sctx.fillText(`Vrail = ${rail.voltage.toFixed(1)}V`, 18 * dpr, rail.y - 18 * dpr);

    // Static symbols
    drawSymbols(sctx, symbols, dpr);
  }, []);

  const step = (dt: number) => {
    const st = stateRef.current; const canvas = dynamicRef.current;
    if (!st || !canvas) return;
    const { w, h, rail, signals } = st; const dpr = dprRef.current;
    const ctx = canvas.getContext('2d'); if (!ctx) return;
    timeRef.current += dt;

    // Clear dynamic layer
    ctx.clearRect(0, 0, w, h);

    // Light wobble of signal control points (lower voltage -> looser)
    for (const sig of signals) {
      for (let i = 0; i < sig.points.length; i++) {
        const p = sig.points[i];
        // Keep x fixed; modulate y slightly
        const a = (1 - sig.stiffness) * 5 * dpr; // amplitude
        const wv = 0.001 + sig.stiffness * 0.003; // frequency
        p.y = p.y0 + a * Math.sin((timeRef.current + i * 350) * wv);
      }
      // Recompute segments and total
      sig.segs.length = 0; sig.total = 0;
      for (let i = 0; i < sig.points.length - 1; i++) {
        const ax = { x: sig.points[i].x, y: sig.points[i].y };
        const bx = { x: sig.points[i + 1].x, y: sig.points[i + 1].y };
        const len = Math.hypot(bx.x - ax.x, bx.y - ax.y);
        sig.segs.push({ ax, bx, len }); sig.total += len;
      }
    }

    // Draw signals (wire) and carriers (+)
    ctx.lineWidth = 1 * dpr;
    ctx.font = `${Math.max(8, 9 * dpr)}px system-ui, -apple-system, Segoe UI, Roboto, sans-serif`;
    ctx.textAlign = 'center'; ctx.textBaseline = 'middle';
    for (const sig of signals) {
      // wire
      ctx.strokeStyle = sig.color;
      ctx.beginPath();
      for (let i = 0; i < sig.points.length; i++) {
        const p = sig.points[i]; if (i === 0) ctx.moveTo(p.x, p.y); else ctx.lineTo(p.x, p.y);
      }
      ctx.stroke();
      // label
      const mid = pointAt(sig, sig.total * 0.08);
      ctx.fillStyle = 'rgba(148,163,184,0.9)';
      ctx.fillText(sig.label, mid.x + 8 * dpr, mid.y - 8 * dpr);

      // carriers
      for (const c of sig.carriers) {
        c.s += c.v * dt; if (c.s > sig.total) c.s -= sig.total;
        const base = pointAt(sig, c.s);

        // Cursor interaction: positive cursor repels positive carriers strongly on low-V signals
        let drawX = base.x, drawY = base.y;
        if (mouse.current.active) {
          const mx = mouse.current.x * dpr, my = mouse.current.y * dpr;
          const dx = drawX - mx, dy = drawY - my; const r2 = dx * dx + dy * dy;
          const R = (sig.tubeR * 5) ** 2; // influence area ~ 5x tube
          if (r2 < R) {
            const inv = 1 / Math.sqrt(r2 + 0.0001);
            const dirx = dx * inv, diry = dy * inv;
            const strength = (1 - r2 / R) * (1.8 - sig.stiffness) * 26 * dpr; // softer if stiffer
            const sign = mouse.current.charge * c.charge; // like charges repel
            drawX += dirx * strength * sign;
            drawY += diry * strength * sign;
          }
        }

        // tube halo when displaced
        const displaced = Math.hypot(drawX - base.x, drawY - base.y) > sig.tubeR * 0.6;
        if (displaced) {
          ctx.save();
          ctx.strokeStyle = sig.color.replace('0.7', '0.25').replace('0.65', '0.25').replace('0.6', '0.25');
          ctx.lineWidth = 2 * dpr;
          ctx.beginPath(); ctx.moveTo(base.x, base.y); ctx.lineTo(drawX, drawY); ctx.stroke();
          ctx.restore();
        }

        // carrier dot + label
        ctx.fillStyle = 'rgba(252,211,77,0.9)'; // amber-ish for positive quasi-charges
        ctx.beginPath(); ctx.arc(drawX, drawY, 1.5 * dpr, 0, Math.PI * 2); ctx.fill();
        ctx.fillStyle = 'rgba(252,211,77,0.95)';
        ctx.fillText('+', drawX, drawY - 0.3 * dpr);
      }
    }

    // Power rail electrons (âˆ’), minimally affected by cursor
    let speedSum = 0;
    ctx.fillStyle = 'rgba(14,165,233,0.95)';
    for (const e of rail.carriers) {
      e.x += e.v * dt; if (e.x > w + 10 * dpr) e.x -= (w + 20 * dpr);
      speedSum += e.v;
      let ex = e.x, ey = rail.y;
      if (mouse.current.active) {
        const mx = mouse.current.x * dpr, my = mouse.current.y * dpr;
        const dx = ex - mx, dy = ey - my; const r2 = dx * dx + dy * dy;
        const R = (80 * dpr) ** 2;
        if (r2 < R) {
          const inv = 1 / Math.sqrt(r2 + 0.0001);
          const dirx = dx * inv, diry = dy * inv;
          // electrons are negative; positive cursor attracts (sign = -1)
          const sign = -mouse.current.charge;
          const strength = (1 - r2 / R) * 6 * dpr; // muted
          ex += dirx * strength * sign;
          ey += diry * strength * sign;
        }
      }
      ctx.beginPath(); ctx.arc(ex, ey, 1.6 * dpr, 0, Math.PI * 2); ctx.fill();
      ctx.fillStyle = 'rgba(14,165,233,0.95)'; ctx.font = `${Math.max(8, 9 * dpr)}px system-ui, -apple-system, Segoe UI, Roboto, sans-serif`;
      ctx.textAlign = 'center'; ctx.textBaseline = 'middle'; ctx.fillText('-', ex, ey - 0.4 * dpr);
    }

    // Current indicator (I) small meter top-right; approximate using speed sum
    const Iraw = speedSum / rail.carriers.length; // px/ms
    const norm = clamp((Iraw - 0.07 * dpr) / (0.12 * dpr), 0, 1);
    const barW = 64 * dpr, barH = 6 * dpr; const bx = w - 90 * dpr, by = rail.y - 22 * dpr;
    ctx.save();
    ctx.fillStyle = 'rgba(30,41,59,0.35)'; ctx.fillRect(bx, by, barW, barH);
    ctx.fillStyle = 'rgba(34,197,94,0.85)'; ctx.fillRect(bx, by, barW * norm, barH);
    ctx.fillStyle = 'rgba(148,163,184,0.9)'; ctx.font = `${Math.max(9, 10 * dpr)}px system-ui, -apple-system, Segoe UI, Roboto, sans-serif`;
    ctx.textAlign = 'left'; ctx.textBaseline = 'bottom';
    ctx.fillText('I (relative)', bx, by - 2 * dpr);
    ctx.restore();

    // Cursor glyph (probe)
    if (mouse.current.active) {
      ctx.save();
      const mx = mouse.current.x * dpr, my = mouse.current.y * dpr;
      const col = mouse.current.charge > 0 ? 'rgba(239,68,68,0.75)' : 'rgba(59,130,246,0.75)';
      ctx.strokeStyle = col; ctx.lineWidth = 1.5 * dpr;
      ctx.beginPath(); ctx.arc(mx, my, 14 * dpr, 0, Math.PI * 2); ctx.stroke();
      ctx.fillStyle = col; ctx.textAlign = 'center'; ctx.textBaseline = 'middle';
      ctx.font = `${Math.max(10, 11 * dpr)}px system-ui, -apple-system, Segoe UI, Roboto, sans-serif`;
      ctx.fillText(mouse.current.charge > 0 ? '+' : '-', mx, my);
      ctx.restore();
    }
  };

  useEffect(() => {
    const sCanvas = staticRef.current; const dCanvas = dynamicRef.current;
    if (!sCanvas || !dCanvas) return;

    if (reduceMotion) { init(); return; }
    init();

    let last = performance.now(), accum = 0;
    const tick = (now: number) => {
      if (!runningRef.current) return;
      const frame = now - last; last = now; accum += frame;
      if (accum >= 33) { const dt = Math.min(33, accum); accum = 0; step(dt); }
      rafRef.current = requestAnimationFrame(tick);
    };
    const start = () => { if (runningRef.current) return; runningRef.current = true; last = performance.now(); rafRef.current = requestAnimationFrame(tick); };
    const stop = () => { runningRef.current = false; if (rafRef.current) cancelAnimationFrame(rafRef.current); rafRef.current = null; };

    // Pause when offscreen
    const io = new IntersectionObserver((entries) => { for (const e of entries) { if (e.isIntersecting) start(); else stop(); } }, { root: null, threshold: 0.05 });
    io.observe(dCanvas);

    // Resize
    const onResize = () => init();
    window.addEventListener('resize', onResize);

    // Mouse: positive by default, hold Alt to flip to negative
    const onMove = (e: MouseEvent) => { mouse.current.x = e.clientX; mouse.current.y = e.clientY; mouse.current.active = true; };
    const onLeave = () => { mouse.current.active = false; };
    const onKey = (e: KeyboardEvent) => { mouse.current.charge = e.altKey ? -1 : 1; };
    window.addEventListener('mousemove', onMove);
    window.addEventListener('mouseleave', onLeave);
    window.addEventListener('keydown', onKey);
    window.addEventListener('keyup', onKey);

    return () => {
      stop(); io.disconnect();
      window.removeEventListener('resize', onResize);
      window.removeEventListener('mousemove', onMove);
      window.removeEventListener('mouseleave', onLeave);
      window.removeEventListener('keydown', onKey);
      window.removeEventListener('keyup', onKey);
    };
  }, [reduceMotion, init]);

  return (
    <div className="absolute inset-0 w-full h-full pointer-events-none select-none" aria-hidden>
      <canvas ref={staticRef} className="absolute inset-0 w-full h-full" />
      <canvas ref={dynamicRef} className="absolute inset-0 w-full h-full" />
    </div>
  );
};

export default CircuitField;
